import React, { Component } from 'react';
import  { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import 'react-bootstrap-table/dist/react-bootstrap-table-all.min.css'


class rowTable extends Component {
    constructor(props) {
        super(props);

        this.options = {
          defaultSortName: 'name',  // default sort column name
          defaultSortOrder: 'desc'  // default sort order
        };
     }

    render() { 

          const cellEditProp = {
            mode: 'click'
          };

        return ( 
            <BootstrapTable  cellEdit={ cellEditProp }  options={ this.options }
                             data={this.props.data} striped hover>
            <TableHeaderColumn isKey dataField='id' dataSort>Product ID</TableHeaderColumn>
            <TableHeaderColumn dataField='name' dataSort>Product Name</TableHeaderColumn>
            <TableHeaderColumn dataField='age' dataSort>Product Price</TableHeaderColumn>
        </BootstrapTable>

         );
    }
}

export  default rowTable;
