import React, { Component } from 'react';




class rowTable extends Component {

    state = {  data : [

               ]
            }

            ColorBlock() {
                let styles = {
                  margin: '20px',
                  width: '50%',
                  height: '250px'
                }
                return styles;
            };

    createHeaderRow () {
        return (
            <thead className="thead-dark">
                <tr>
                    <th> id </th>
                    <th> name </th>
                    <th> age </th>
                </tr>
            </thead>
        )
    };

    tableStyle () {
        let classes = "table table-sm table-striped table-hover table-bordered";

        return classes;
    }

    createRow () {
        if (this.state.data.length === 0) return "No Data Found";
        return (

                this.state.data.map(data => 
                                      <React.Fragment>
                                       <tr>
                                        <td>{data.id}</td>
                                        <td>{data.name}</td>
                                        <td>{data.age}</td>
                                        </tr>
                                    </React.Fragment>
                                    ) 


        )
    };

    render() { 
        return ( 
                <React.Fragment>
                <table style = {this.ColorBlock()} className = {this.tableStyle()}>
                    {this.createHeaderRow()}
                    <tbody>
                        {this.createRow()}
                    </tbody>
                </table>
               </React.Fragment>
         );
    }
}

export  default rowTable;
