import React, { Component } from 'react';
import  { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';

class rowTable extends Component {


    ColorBlock() {
        let styles = {
            'margin': '5px 20px 0px 20px',
            'width': '50%'
        }
        return styles;
    };

    createHeaderRow () {
        return (
            <thead className="thead-dark">
                <tr>
                    <th> id </th>
                    <th> name </th>
                    <th> age </th>
                    <th>  </th>
                </tr>
            </thead>
        )
    };

    tableStyle () {
        let classes = "table table-sm table-striped table-hover table-bordered";

        return classes;
    }

    emptyStyle () {
        let styles = {
            'text-align': 'center'
          }
          return styles;        
    }

    createRow () {
        if (this.props.data.length === 0) return <tr  style = {this.emptyStyle()}>  <td colSpan={3}>No Data Found</td></tr> ;
        return (

                this.props.data.map(data => 
                                      <React.Fragment>
                                       <tr>
                                        <td>{data.id}</td>
                                        <td>{data.name}</td>
                                        <td>{data.age}</td>
                                        <td>
                                            <button className="btn btn-sm btn-primary m-2">Edit</button>
                                            <button className="btn btn-sm btn-danger m-2">Delete</button>
                                        </td>
                                        </tr>
                                    </React.Fragment>
                                    ) 


        )
    };

    render() { 

        var products = [{
            id: 1,
            name: "Product1",
            price: 120}, 

            {
            id: 2,
            name: "Product2",
            price: 80
        }];

        console.log(this.props.data);
        const options = {
            page: 2,  
            sizePerPageList: [ {
              text: '5', value: 5
            }, {
              text: '10', value: 10
            }, {
              text: 'All', value: products
            } ], 
            sizePerPage: 5,  
            pageStartIndex: 0, 
            paginationSize: 3, 
            prePage: 'Prev', 
            nextPage: 'Next', 
            firstPage: 'First',
            lastPage: 'Last', 
            paginationPosition: 'top'

          };

        return ( 
                     <BootstrapTable data={ products }
                                     pagination={ true } options={ options }>
                        <TableHeaderColumn dataField='id' isKey>Employe eID</TableHeaderColumn>
                        <TableHeaderColumn dataField='name'>Employe Name</TableHeaderColumn>
                    </BootstrapTable>
         );
    }
}

export  default rowTable;
