import React, { Component } from 'react';
import  { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import 'react-bootstrap-table/dist/react-bootstrap-table-all.min.css'

function getCaret(direction) {
    if (direction === 'asc') {
      return (
        <span> asc</span>
      );
    }
    if (direction === 'desc') {
      return (
        <span> desc</span>
      );
    }
    return (
      <span> asc/desc</span>
    );
  }

  function onRowSelect(row, isSelected, e) {
    let rowStr = '';
    for (const prop in row) {
      rowStr += prop + ': "' + row[prop] + '"';
    }
    console.log(e);
    alert(`is selected: ${isSelected}, ${rowStr}`);
  }

  function onSelectAll(isSelected, rows) {
    alert(`is select all: ${isSelected}`);
    if (isSelected) {
      alert('Current display and selected data: ');
    } else {
      alert('unselect rows: ');
    }
    for (let i = 0; i < rows.length; i++) {
      alert(rows[i].id);
    }
  }

class rowTable extends Component {
    constructor(props) {
        super(props);

        this.options = {
          defaultSortName: 'age',  // default sort column name
          defaultSortOrder: 'desc',  // default sort order
          onSortChange: this.onSortChange
        };
     }


    onSortChange(sortName, sortOrder) {
        console.info('onSortChange', arguments);
    }

    render() { 

          const cellEditProp = {
            mode: 'click'
          };

          const selectRowProp = {
            mode: 'checkbox',
            clickToSelect: true,
            unselectable: [ 1, 3 ],
            selected: [2],
            onSelect: onRowSelect,
            onSelectAll: onSelectAll,
            bgColor: function(row, isSelect) {
                if (isSelect) {
                  const { id } = row;
                  if (id < 2) return 'blue';
                  else if (id < 4) return 'red';
                  else return 'yellow';
                }
                return null;
              }
          };

        return ( 
            <div>
                {/* <button onClick={ this.handleBtnClick } className='btn btn-default'>Click to apply text filter</button> */}
                <BootstrapTable selectRow={ selectRowProp }  cellEdit={ cellEditProp }   multiColumnSort={ 2 }
                                options={ this.options }
                                data={this.props.data} striped hover>
                <TableHeaderColumn width='90'  tdStyle={ { whiteSpace: 'normal' } } thStyle={ { 'fontWeight': 'lighter' } } headerAlign='center' dataAlign='center' isKey dataField='id' caretRender={ getCaret } dataSort>Product ID</TableHeaderColumn>
                <TableHeaderColumn width='200' tdStyle={ { whiteSpace: 'normal' } } thStyle={ { 'fontWeight': 'lighter' } } headerAlign='center' dataAlign='center' filter={ { type: 'TextFilter', delay: 500 }} dataField='name' dataSort>Product Name</TableHeaderColumn>
                <TableHeaderColumn width='150' tdStyle={ { whiteSpace: 'normal' } } thStyle={ { 'fontWeight': 'lighter' } } headerAlign='center' dataAlign='center' filter={ { type: 'NumberFilter', delay: 1000, numberComparators: [ '=', '>', '<=' ] } } dataField='age' dataSort>Product Price</TableHeaderColumn>
                 </BootstrapTable>
        </div>
         );
    }
}

export  default rowTable;
