import React, { Component } from 'react';
import  { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import 'react-bootstrap-table/dist/react-bootstrap-table-all.min.css'

class ActiveFormatter extends React.Component {
    render() {
      return (
        <input type='checkbox' checked={ this.props.active }/>
      );
    }
  }

  function activeFormatter(cell, row) {
    return (
      <ActiveFormatter active={ cell } />
    );
  }

function getCaret(direction) {
    if (direction === 'asc') {
      return (
        <span> asc</span>
      );
    }
    if (direction === 'desc') {
      return (
        <span> desc</span>
      );
    }
    return (
      <span> asc/desc</span>
    );
  }

class rowTable extends Component {
    constructor(props) {
        super(props);

        this.options = {
          defaultSortName: 'age',  // default sort column name
          defaultSortOrder: 'desc',  // default sort order
          onSortChange: this.onSortChange
        };
     }

  onSortChange(sortName, sortOrder) {
    console.info('onSortChange', arguments);
  }

    render() { 

          const cellEditProp = {
            mode: 'click'
          };

        return ( 
            <BootstrapTable  cellEdit={ cellEditProp }   multiColumnSort={ 2 }
                             options={ this.options }
                             data={this.props.data} striped hover>
            <TableHeaderColumn dataField='active' dataFormat={ activeFormatter }>Active</TableHeaderColumn>
            <TableHeaderColumn width='90'  tdStyle={ { whiteSpace: 'normal' } } thStyle={ { 'fontWeight': 'lighter' } } headerAlign='center' dataAlign='center' isKey dataField='id' caretRender={ getCaret } dataSort>Product ID</TableHeaderColumn>
            <TableHeaderColumn width='200' tdStyle={ { whiteSpace: 'normal' } } thStyle={ { 'fontWeight': 'lighter' } } headerAlign='center' dataAlign='center' dataField='name' dataSort>Product Name</TableHeaderColumn>
            <TableHeaderColumn width='150' tdStyle={ { whiteSpace: 'normal' } } thStyle={ { 'fontWeight': 'lighter' } } headerAlign='center' dataAlign='center' dataField='age' dataSort>Product Price</TableHeaderColumn>
        </BootstrapTable>

         );
    }
}

export  default rowTable;
