import React, { Component } from 'react';

class rowTable extends Component {

    ColorBlock() {
        let styles = {
            'margin': '5px 20px 0px 20px',
            'width': '50%'
        }
        return styles;
    };

    createHeaderRow () {
        return (
            <thead className="thead-dark">
                <tr>
                    <th> id </th>
                    <th> name </th>
                    <th> age </th>
                    <th>  </th>
                </tr>
            </thead>
        )
    };

    tableStyle () {
        let classes = "table table-sm table-striped table-hover table-bordered";

        return classes;
    }

    emptyStyle () {
        let styles = {
            'text-align': 'center'
          }
          return styles;        
    }

    createRow () {
        if (this.props.data.length === 0) return <tr  style = {this.emptyStyle()}>  <td colSpan={3}>No Data Found</td></tr> ;
        return (

                this.props.data.map(data => 
                                      <React.Fragment>
                                       <tr>
                                        <td>{data.id}</td>
                                        <td>{data.name}</td>
                                        <td>{data.age}</td>
                                        <td>
                                            <button className="btn btn-sm btn-primary m-2">Edit</button>
                                            <button className="btn btn-sm btn-danger m-2">Delete</button>
                                        </td>
                                        </tr>
                                    </React.Fragment>
                                    ) 


        )
    };

    render() { 
        const options = {
            page: 2,  // which page you want to show as default
            sizePerPageList: [ {
              text: '5', value: 5
            }, {
              text: '10', value: 10
            }, {
              text: 'All', value: products.length
            } ], // you can change the dropdown list for size per page
            sizePerPage: 5,  // which size per page you want to locate as default
            pageStartIndex: 0, // where to start counting the pages
            paginationSize: 3,  // the pagination bar size.
            prePage: 'Prev', // Previous page button text
            nextPage: 'Next', // Next page button text
            firstPage: 'First', // First page button text
            lastPage: 'Last', // Last page button text
            paginationShowsTotal: this.renderShowsTotal,  // Accept bool or function
            paginationPosition: 'top'  // default is bottom, top and both is all available
            // hideSizePerPage: true > You can hide the dropdown for sizePerPage
            // alwaysShowAllBtns: true // Always show next and previous button
            // withFirstAndLast: false > Hide the going to First and Last page button
          };



        return ( 
                 <div className="table-responsive-sm">
                    <table style = {this.ColorBlock()} className = {this.tableStyle()}>
                        {this.createHeaderRow()}
                        <tbody>
                            {this.createRow()}
                        </tbody>
                    </table>
                </div>
         );
    }
}

export  default rowTable;
