import React, { Component } from 'react';

class rowTable extends Component {

    state = {  data : [
                // {name : "oday", id : 1, age : 28},
                // {name : "oday", id : 2, age : 22},
                // {name : "ofay", id : 3, age : 23},
                // {name : "oday", id : 4, age : 24}
               ]
            }

            ColorBlock() {
                let styles = {
                  'margin': '20px 20px 0px 20px',
                  'width': '50%'
                }
                return styles;
            };

    createHeaderRow () {
        return (
            <thead className="thead-dark">
                <tr>
                    <th> id </th>
                    <th> name </th>
                    <th> age </th>
                </tr>
            </thead>
        )
    };

    tableStyle () {
        let classes = "table table-sm table-striped table-hover table-bordered";

        return classes;
    }

    emptyStyle () {
        let styles = {
            'text-align': 'center'
          }
          return styles;        
    }

    createRow () {
        if (this.state.data.length === 0) return <tr  style = {this.emptyStyle()}>  <td colSpan={3}>No Data Found</td></tr> ;
        return (

                this.state.data.map(data => 
                                      <React.Fragment>
                                       <tr>
                                        <td>{data.id}</td>
                                        <td>{data.name}</td>
                                        <td>{data.age}</td>
                                        </tr>
                                    </React.Fragment>
                                    ) 


        )
    };

    render() { 
        return ( 
                 <div className="table-responsive-sm">
                    <table style = {this.ColorBlock()} className = {this.tableStyle()}>
                        {this.createHeaderRow()}
                        <tbody>
                            {this.createRow()}
                        </tbody>
                    </table>
                </div>
         );
    }
}

export  default rowTable;
