import React, { Component } from 'react';
import EmployeeTable from './employeeTable';
import ModalConductor from './modalConductor';

class curdEmployee extends Component {
    state = { type : '',
          data : [
        {name : "oday", id : 1, age : 28},
        {name : "oday", id : 2, age : 22},
        {name : "ofay", id : 3, age : 23},
        {name : "oday", id : 4, age : 24}
      ]
     };

     handleAddEmployee = type => {
        console.log(type);
        // this.setState ({type : 'add'});
     };


    render() { 
        return ( 

            <div>
                <button onClick = {() => this.handleEmployeeOperation('add')} 
                        style={this.btnStyle()} 
                        className="btn btn-primary btn-sm" >
                    add
                </button>
                <EmployeeTable data={this.state.data}/>
            </div>

         );
    }

    btnStyle() {
        let styles = {
            'margin': '20px 20px 0px 20px'
          }
          return styles;        
    }

    handleOpenModal () {

        if (this.state.type === '') return;

        return (<ModalConductor currentModal = {this.state.type}/>)
    }
}

export default curdEmployee;
