import React, { Component } from 'react';
import TableBootstrap from './tableBootstrap';
import ModalConductor from './modalConductor';
import axios from 'axios';

class curdEmployee extends Component {
    state = { type : "",
              modalShow: false ,
          data : [
            {name : "oday", id : 1, age : 28},
            {name : "oday", id : 2, age : 22},
            {name : "ofay", id : 3, age : 23},
            {name : "oday", id : 4, age : 24}
      ]
     };

     componentDidMount(){
        axios.get('https://curdperson.herokuapp.com/api/persons')
        .then(response => {
          this.setState({ data: response.data });
        }).catch(function (error) {
          console.log(error);
        });
      }

     handleEmployeeOperation = type => {
         this.setState ({type : type});
         this.setState ({modalShow : true});
     };

     handleClose = () => {
        this.setState ({modalShow : false});
    };

    handleAddEmployee= (name , age) => {
         this.setState ({data : [...this.state.data , {name : name, id : 99, age : age}]});
    };

    render() { 
        return ( 
            <React.Fragment>
                {this.handleOpenModal()}
                <div>
                    {/* <button onClick = {() => this.handleEmployeeOperation("ADD_EMPLOYEE")} 
                            style={this.btnStyle()} 
                            className="btn btn-primary btn-sm" >
                        add
                    </button> */}
                    <TableBootstrap data={this.state.data}/>
                </div>
         </React.Fragment>
         );
    }

    btnStyle() {
        let styles = {
            'margin': '20px 20px 0px 20px'
          }
          return styles;        
    }

    handleOpenModal () {
        console.log('handleOpenModal');
        if (this.state.type === '') return;
        console.log('handleOpenModal 11');
        return (<ModalConductor onAddEmployee = {this.handleAddEmployee} onHandleClose = {this.handleClose} {...this.state}/>);
    }
}

export default curdEmployee;
