import React, { Component } from 'react';
import  { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import 'react-bootstrap-table/dist/react-bootstrap-table-all.min.css'
import './css/style.css';
import './css/style.css';

class rowTable extends Component {
    ColorBlock() {
        let styles = {
            'margin': '5px 20px 0px 20px',
            'width': '50%'
        }
        return styles;
    };

    createHeaderRow () {
        return (
            <thead className="thead-dark">
                <tr>
                    <th> id </th>
                    <th> name </th>
                    <th> age </th>
                    <th>  </th>
                </tr>
            </thead>
        )
    };

    tableStyle () {
        let classes = "table table-sm table-striped table-hover table-bordered";

        return classes;
    }

    emptyStyle () {
        let styles = {
            'text-align': 'center'
          }
          return styles;        
    }

    render() { 


        var products = [{
            id: 1,
            name: "Product1",
            price: 120}, 

            {
            id: 2,
            name: "Product2",
            price: 80
        }];

        console.log(this.props.data);
        const options = {
            page: 2,  
            sizePerPageList: [ {
              text: '5', value: 5
            }, {
              text: '10', value: 10
            }, {
              text: 'All', value: products
            } ], 
            sizePerPage: 5,  
            pageStartIndex: 0, 
            paginationSize: 3, 
            prePage: 'Prev', 
            nextPage: 'Next', 
            firstPage: 'First',
            lastPage: 'Last', 
            paginationPosition: 'top'

          };

          const cellEditProp = {
            mode: 'click'
          };

        return ( 
            <div className="table-responsive-sm">
            <BootstrapTable style = {this.ColorBlock()} cellEdit={ cellEditProp } className = {this.tableStyle()} data={this.props.data} striped hover>
            <TableHeaderColumn isKey dataField='id'>Product ID</TableHeaderColumn>
            <TableHeaderColumn dataField='name'>Product Name</TableHeaderColumn>
            <TableHeaderColumn dataField='age'>Product Price</TableHeaderColumn>
        </BootstrapTable>
        </div>
         );
    }
}

export  default rowTable;
